	def get_image_path(instance, filename):
		"""
		Puts image in MEDIA_ROOT/photos/instance_id/file
		"""
		app_name = 'None'
		if isinstance(instance, Article):
			app_name = "Article"
		elif isinstance(instance, ArticleImage):
			app_name = "ArticleImage"
		return 'images/%s/%s/%s' % (app_name, instance.id, filename)

	class Article(models.Model):
		STATUS_CHOICES = (
			(0, '发布'),
			(1, '删除'),
			(2, '下架')
		)
		source = models.CharField(max_length=30, blank=True, null=True)
		title = models.CharField(max_length=200)
		body = models.TextField()
		abstract = models.CharField(max_length=255, blank=True)
		time_created = models.DateTimeField(default=timezone.now)
		author_id = models.PositiveIntegerField()
		ad_id = models.PositiveIntegerField(null=True)
		# image_id = models.PositiveIntegerField(null=True)
		# front_image = models.ImageField(upload_to='images/%Y/%m/%d', null=True)
		front_image = models.ImageField(upload_to=get_image_path, null=True)
		status = models.PositiveIntegerField(choices=STATUS_CHOICES, default=0)

		class Meta:
			ordering = ['-time_created']

		@property
		def article_images(self):
			return ArticleImage.objects.filter(article_id=self.id)


	class ArticleImage(models.Model):
		"""
		文章图片表
		"""
		article_id = models.PositiveIntegerField()
		image = models.ImageField(upload_to=get_image_path, max_length=254)

		def __str__(self):
			return '%d: %s' % (self.article_id, self.image.url)
		

