	class ArticlesAPIView(APIView):
		"""
		API endpoint that represents a list of articles
		"""
		serializer_class = ArticleSerializer
		# permission_classes = (permissions.IsAuthenticatedOrReadOnly,)

		# authentication_classes = ()

		def get(self, request, format=None):
			"""return  a list of articles
			"""
			page_size = request.query_params.get('page_size', 10)
			page = request.query_params.get('page', 1)
			article_list = Article.objects.all()
			paginator = Paginator(article_list, page_size)
			total_pages = paginator.num_pages
			try:
				articles = paginator.page(page)
			except PageNotAnInteger:
				articles = paginator.page(1)
			except EmptyPage:
				articles = paginator.page(total_pages)

			fields = ('id', 'title', 'abstract', 'time_created', 'author', 'status',
			'front_image', 'comment_num', 'read_num', 'favor_num', 'can_favor',
			'article_images'
			)
			serializer = self.serializer_class(articles, many=True,fields=fields,context={'request': request})
			return Response(serializer.data, status=status.HTTP_200_OK)

		def post(self, request, format=None):
			serializer = self.serializer_class(data=request.data)
			if serializer.is_valid():
				serializer.save()
				return Response(serializer.data, status=status.HTTP_201_CREATED)
			return Response(serializer.errors, status=status.HTTP_400_BAD_REQUEST)